mapper.select:
select rpad(substr(userid,0,2),length(userid),'*') as userid1, userid
from member
where username=#{username} and phone=#{phone} and email=#{email}

select count(*)
from member
where userid=#{userid}

mapper.delete:
delete 
from member
where userid=#{userid} and passwd=#{passwd}

mapper.select:
select count(*)
from member
where userid=#{userid} and username=#{username}

select count(*)
from member
where userid=#{userid} and username=#{username} and email=#{email}

mapper.update:
update member
set passwd=#{passwd}
where userid=#{userid}
